import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Alert, Container, Row, Col, Table, Badge, Spinner, ButtonGroup, Form } from 'react-bootstrap';
import { QrCodeScan, CheckCircleFill, XCircleFill, Calendar, Clock, ArrowRightCircle, ArrowLeftCircle } from 'react-bootstrap-icons';
import { useAuth } from '../../hooks/useAuth';
import { secureApiEndpoints } from '../../utils/apiMigration';
import QrScanner from 'qr-scanner';
import Swal from 'sweetalert2';

const AttendanceScanner = () => {
  const [isScanning, setIsScanning] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [todayAttendances, setTodayAttendances] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [scannerError, setScannerError] = useState('');
  const [showManualInput, setShowManualInput] = useState(false);
  const [manualQrCode, setManualQrCode] = useState('');
  const [isMarkingAbsent, setIsMarkingAbsent] = useState(false);
  const [eventType, setEventType] = useState('auto'); // 'entry', 'exit' ou 'auto'
  const [entryExitStats, setEntryExitStats] = useState(null);

  const { user } = useAuth();
  const videoRef = useRef(null);
  const qrScannerRef = useRef(null);

  useEffect(() => {
    loadTodayAttendances();
    loadEntryExitStats();
    return () => {
      if (qrScannerRef.current) {
        qrScannerRef.current.destroy();
      }
    };
  }, []);

  const loadTodayAttendances = async () => {
    try {
      setIsLoading(true);
      const response = await secureApiEndpoints.supervisors.getDailyAttendance({
        supervisor_id: user.id
      });

      if (response.success) {
        setTodayAttendances(response.data.attendances || []);
      } else {
        console.error('Erreur API:', response.message);
      }
    } catch (error) {
      console.error('Erreur lors du chargement des prÃ©sences:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loadEntryExitStats = async () => {
    try {
      const response = await secureApiEndpoints.supervisors.getEntryExitStats({
        supervisor_id: user.id
      });

      if (response.success) {
        setEntryExitStats(response.data);
      } else {
        console.error('Erreur API stats:', response.message);
      }
    } catch (error) {
      console.error('Erreur lors du chargement des statistiques:', error);
    }
  };

  const startScanner = async () => {
    try {
      setScannerError('');
      setIsScanning(true);

      console.log('ğŸ” Tentative de dÃ©marrage du scanner...');

      // VÃ©rifier d'abord les permissions de camÃ©ra
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        console.log('âœ… Permissions camÃ©ra accordÃ©es');
        stream.getTracks().forEach(track => track.stop()); // ArrÃªter le stream de test
      } catch (permError) {
        console.error('âŒ Permissions camÃ©ra refusÃ©es:', permError);
        throw new Error('Permission denied: ' + permError.message);
      }

      // VÃ©rifier les camÃ©ras disponibles
      const cameras = await QrScanner.listCameras(true);
      console.log('ğŸ“· CamÃ©ras disponibles:', cameras);

      if (cameras.length === 0) {
        throw new Error('Aucune camÃ©ra trouvÃ©e sur cet appareil');
      }

      if (videoRef.current) {
        // Configuration plus permissive
        qrScannerRef.current = new QrScanner(
          videoRef.current,
          (result) => handleScanResult(result.data),
          {
            onDecodeError: error => {
              // RÃ©duire le bruit des erreurs de dÃ©codage
              // console.log('Scan decode error:', error);
            },
            highlightScanRegion: true,
            highlightCodeOutline: true,
            preferredCamera: cameras.length > 1 ? 'environment' : cameras[0].id, // Utiliser la premiÃ¨re camÃ©ra si une seule
            maxScansPerSecond: 3, // RÃ©duire pour Ã©viter la surcharge
            returnDetailedScanResult: false,
          }
        );

        console.log('â³ DÃ©marrage du scanner QR...');
        await qrScannerRef.current.start();
        console.log('âœ… Scanner QR dÃ©marrÃ© avec succÃ¨s');

        // Masquer le formulaire de saisie manuelle si le scanner fonctionne
        setShowManualInput(false);
      }
    } catch (error) {
      console.error('âŒ Erreur lors du dÃ©marrage du scanner:', error);
      console.error('Type d\'erreur:', error.constructor.name);
      console.error('Message:', error.message);

      let errorMessage = 'Impossible d\'accÃ©der Ã  la camÃ©ra.';
      let debugInfo = '';

      if (error.name === 'NotAllowedError' || error.message.includes('Permission denied')) {
        errorMessage = 'ğŸš« AccÃ¨s Ã  la camÃ©ra refusÃ© par le navigateur.';
        debugInfo = 'Cliquez sur l\'icÃ´ne ğŸ”’ dans la barre d\'adresse et autorisez la camÃ©ra.';
      } else if (error.name === 'NotFoundError' || error.message.includes('Camera not found')) {
        errorMessage = 'ğŸ“· Aucune camÃ©ra trouvÃ©e.';
        debugInfo = 'VÃ©rifiez qu\'une camÃ©ra est connectÃ©e et fonctionnelle.';
      } else if (error.name === 'NotSupportedError') {
        errorMessage = 'ğŸŒ Votre navigateur ne supporte pas l\'accÃ¨s Ã  la camÃ©ra.';
        debugInfo = 'Essayez avec Chrome, Firefox ou Safari rÃ©cent.';
      } else if (error.name === 'NotReadableError') {
        errorMessage = 'âš ï¸ CamÃ©ra occupÃ©e par une autre application.';
        debugInfo = 'Fermez les autres applications utilisant la camÃ©ra.';
      } else {
        errorMessage = 'ğŸ”§ Erreur technique du scanner.';
        debugInfo = `DÃ©tails: ${error.message}`;
      }

      setScannerError(`${errorMessage} ${debugInfo}`);
      setIsScanning(false);

      // Proposer l'alternative de saisie manuelle
      setShowManualInput(true);
    }
  };

  const stopScanner = () => {
    if (qrScannerRef.current) {
      qrScannerRef.current.stop();
      qrScannerRef.current.destroy();
      qrScannerRef.current = null;
    }
    setIsScanning(false);
    setShowManualInput(false);
  };

  const handleManualSubmit = async (e) => {
    e.preventDefault();
    if (!manualQrCode.trim()) return;

    await handleScanResult(manualQrCode.trim());
    setManualQrCode('');
    setShowManualInput(false);
  };

  const runCameraDiagnostic = async () => {
    console.log('ğŸ” === DIAGNOSTIC CAMÃ‰RA ===');

    try {
      // Test 1: VÃ©rifier le support MediaDevices
      if (!navigator.mediaDevices) {
        console.log('âŒ navigator.mediaDevices non supportÃ©');
        return;
      }
      console.log('âœ… navigator.mediaDevices supportÃ©');

      // Test 2: Lister les appareils
      const devices = await navigator.mediaDevices.enumerateDevices();
      const cameras = devices.filter(device => device.kind === 'videoinput');
      console.log('ğŸ“· CamÃ©ras dÃ©tectÃ©es:', cameras.length);
      cameras.forEach((camera, index) => {
        console.log(`  ${index + 1}. ${camera.label || 'CamÃ©ra sans nom'} (${camera.deviceId})`);
      });

      // Test 3: Test d'accÃ¨s simple
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ 
          video: { 
            width: 640, 
            height: 480,
            facingMode: 'environment' 
          } 
        });
        console.log('âœ… AccÃ¨s camÃ©ra rÃ©ussi');
        console.log('ğŸ“¹ Stream:', stream.getVideoTracks()[0].getSettings());
        stream.getTracks().forEach(track => track.stop());
      } catch (streamError) {
        console.log('âŒ Ã‰chec accÃ¨s camÃ©ra:', streamError.name, streamError.message);
      }

      // Test 4: Test QrScanner
      try {
        const qrCameras = await QrScanner.listCameras(true);
        console.log('ğŸ“± QrScanner camÃ©ras:', qrCameras.length);
        qrCameras.forEach((camera, index) => {
          console.log(`  ${index + 1}. ${camera.label} (${camera.id})`);
        });
      } catch (qrError) {
        console.log('âŒ QrScanner erreur:', qrError.message);
      }

    } catch (error) {
      console.log('âŒ Erreur diagnostic:', error);
    }

    console.log('ğŸ” === FIN DIAGNOSTIC ===');
  };

  const handleMarkAllAbsent = async () => {
    const result = await Swal.fire({
      title: 'ğŸ“‹ Marquer les absents ?',
      html: `
        <p>Cette action va marquer comme <strong>absents</strong> tous les Ã©lÃ¨ves qui n'ont pas Ã©tÃ© enregistrÃ©s comme prÃ©sents aujourd'hui.</p>
        <div class="alert alert-info mt-3">
          <strong>â„¹ï¸ Note :</strong> Seuls les Ã©lÃ¨ves qui n'ont <strong>aucune entrÃ©e</strong> aujourd'hui seront marquÃ©s absents.
        </div>
        <p><strong>ÃŠtes-vous sÃ»r de vouloir continuer ?</strong></p>
      `,
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
      confirmButtonText: 'âœ… Oui, marquer les absents',
      cancelButtonText: 'âŒ Annuler'
    });

    if (result.isConfirmed) {
      setIsMarkingAbsent(true);

      try {
        const response = await secureApiEndpoints.supervisors.markAllAbsentStudents({
          supervisor_id: user.id,
          attendance_date: new Date().toISOString().split('T')[0] // Format YYYY-MM-DD
        });

        if (response.success) {
          // Afficher le rÃ©sultat dÃ©taillÃ©
          const classStatsHtml = Object.entries(response.data.class_statistics || {})
            .map(([className, count]) => `<li><strong>${className}:</strong> ${count} absent(s)</li>`)
            .join('');

          await Swal.fire({
            title: 'âœ… Absences marquÃ©es !',
            html: `
              <div class="text-left">
                <p><strong>${response.data.absent_students_marked}</strong> Ã©lÃ¨ve(s) marquÃ©(s) comme absent(s)</p>
                <hr>
                <p><strong>ğŸ“Š RÃ©sumÃ© du jour :</strong></p>
                <ul class="list-unstyled">
                  <li>ğŸ‘¥ <strong>Total Ã©lÃ¨ves :</strong> ${response.data.total_students}</li>
                  <li>âœ… <strong>PrÃ©sents :</strong> ${response.data.present_students}</li>
                  <li>âŒ <strong>Absents marquÃ©s :</strong> ${response.data.absent_students_marked}</li>
                </ul>
                ${classStatsHtml ? `
                <hr>
                <p><strong>ğŸ“‹ Par classe :</strong></p>
                <ul>${classStatsHtml}</ul>
                ` : ''}
                <div class="alert alert-success mt-3">
                  <small>ğŸ“± Les parents ont Ã©tÃ© notifiÃ©s automatiquement par WhatsApp</small>
                </div>
              </div>
            `,
            icon: 'success',
            confirmButtonText: 'Parfait !'
          });

          // Recharger les donnÃ©es
          loadTodayAttendances();
          loadEntryExitStats();
        } else {
          Swal.fire({
            title: 'Erreur',
            text: response.message || 'Erreur lors du marquage des absences',
            icon: 'error'
          });
        }
      } catch (error) {
        console.error('Erreur marquage absences:', error);
        Swal.fire({
          title: 'Erreur',
          text: 'Erreur lors du marquage des absences',
          icon: 'error'
        });
      } finally {
        setIsMarkingAbsent(false);
      }
    }
  };

  const handleScanResult = async (qrCode) => {
    try {
      stopScanner();
      setIsLoading(true);

      const response = await secureApiEndpoints.supervisors.scanQR({
        student_qr_code: qrCode,
        supervisor_id: user.id,
        event_type: eventType
      });

      if (response.success) {
        const eventIcon = response.data.event_type === 'entry' ? 'ğŸŸ¢' : 'ğŸ”´';
        setMessage(`${eventIcon} ${response.data.event_label} de ${response.data.student_name} enregistrÃ©e Ã  ${response.data.marked_at}`);
        setMessageType('success');
        loadTodayAttendances(); // Recharger la liste
        loadEntryExitStats(); // Recharger les statistiques
      } else {
        // Afficher le message d'erreur dÃ©taillÃ©
        let errorMessage = response.message;
        if (response.student_name) {
          errorMessage = `${response.student_name}: ${response.message}`;
        }
        setMessage(`âŒ ${errorMessage}`);
        setMessageType('danger');
      }
    } catch (error) {
      console.error('Erreur scan:', error);

      // Tenter d'extraire un message d'erreur plus dÃ©taillÃ©
      let errorMessage = 'Erreur lors de l\'enregistrement de la prÃ©sence';

      if (error.response && error.response.data) {
        if (error.response.data.message) {
          errorMessage = error.response.data.message;
        } else if (error.response.data.error_details) {
          errorMessage = `Erreur technique: ${error.response.data.error_details}`;
        }
      } else if (error.message) {
        errorMessage = error.message;
      }

      setMessage(`âŒ ${errorMessage}`);
      setMessageType('danger');
    } finally {
      setIsLoading(false);
      // Auto-clear message after 5 seconds
      setTimeout(() => {
        setMessage('');
        setMessageType('');
      }, 5000);
    }
  };

  const formatTime = (timeString) => {
    if (!timeString) return '';
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString('fr-FR', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <Container fluid className="py-4">
      <Row>
        <Col>
          <h2 className="mb-4 d-flex align-items-center">
            <QrCodeScan className="me-2" />
            Scanner de PrÃ©sences
          </h2>
        </Col>
      </Row>

      {/* Scanner Section */}
      <Row className="mb-4">
        <Col lg={6}>
          <Card>
            <Card.Header>
              <h5 className="mb-0 d-flex align-items-center">
                <QrCodeScan className="me-2" />
                Scanner QR
              </h5>
            </Card.Header>
            <Card.Body>
              {scannerError && (
                <Alert variant="danger" className="mb-3">
                  <div className="d-flex align-items-start">
                    <div className="me-2">ğŸ“·</div>
                    <div>
                      <strong>ProblÃ¨me de camÃ©ra :</strong><br />
                      {scannerError}
                      <hr className="my-2" />
                      <small>
                        <strong>Solutions :</strong><br />
                        â€¢ Utilisez la <strong>saisie manuelle</strong> ci-dessous<br />
                        â€¢ Cliquez sur l'icÃ´ne ğŸ”’ dans la barre d'adresse et autorisez la camÃ©ra<br />
                        â€¢ Actualisez la page aprÃ¨s avoir autorisÃ©<br />
                        â€¢ Fermez les autres onglets/apps utilisant la camÃ©ra
                      </small>
                      <hr className="my-2" />
                      <Button 
                        variant="outline-info" 
                        size="sm"
                        onClick={runCameraDiagnostic}
                      >
                        ğŸ” Diagnostic camÃ©ra (voir console)
                      </Button>
                    </div>
                  </div>
                </Alert>
              )}

              {message && (
                <Alert variant={messageType} className="mb-3">
                  {message}
                </Alert>
              )}

              {/* Event Type Selection */}
              <div className="mb-4">
                <h6 className="mb-3">Mode de scan :</h6>
                <ButtonGroup className="w-100">
                  <Button
                    variant={eventType === 'auto' ? 'primary' : 'outline-primary'}
                    onClick={() => setEventType('auto')}
                    disabled={isScanning}
                    size="sm"
                  >
                    ğŸ¤– Automatique
                  </Button>
                  <Button
                    variant={eventType === 'entry' ? 'success' : 'outline-success'}
                    onClick={() => setEventType('entry')}
                    disabled={isScanning}
                    size="sm"
                  >
                    <ArrowRightCircle className="me-1" size={14} />
                    EntrÃ©e
                  </Button>
                  <Button
                    variant={eventType === 'exit' ? 'danger' : 'outline-danger'}
                    onClick={() => setEventType('exit')}
                    disabled={isScanning}
                    size="sm"
                  >
                    <ArrowLeftCircle className="me-1" size={14} />
                    Sortie
                  </Button>
                </ButtonGroup>
                <div className="mt-2">
                  <small className="text-muted">
                    {eventType === 'auto' && 'ğŸ¤– Le systÃ¨me dÃ©terminera automatiquement si c\'est une entrÃ©e ou sortie'}
                    {eventType === 'entry' && 'ğŸŸ¢ Mode entrÃ©e forcÃ©e'}
                    {eventType === 'exit' && 'ğŸ”´ Mode sortie forcÃ©e'}
                  </small>
                </div>
              </div>

              <div className="text-center mb-3">
                {!isScanning ? (
                  <div className="d-flex flex-column gap-2">
                    <Button 
                      variant="primary" 
                      size="lg"
                      onClick={startScanner}
                      disabled={isLoading}
                    >
                      {isLoading ? (
                        <>
                          <Spinner size="sm" className="me-2" />
                          Traitement...
                        </>
                      ) : (
                        <>
                          <QrCodeScan className="me-2" />
                          DÃ©marrer le Scanner
                        </>
                      )}
                    </Button>

                    {!showManualInput ? (
                      <Button 
                        variant="outline-secondary" 
                        size="sm"
                        onClick={() => setShowManualInput(true)}
                        disabled={isLoading}
                      >
                        ğŸ“ Saisie manuelle
                      </Button>
                    ) : (
                      <Button 
                        variant="outline-danger" 
                        size="sm"
                        onClick={() => setShowManualInput(false)}
                      >
                        âŒ Annuler saisie
                      </Button>
                    )}
                  </div>
                ) : (
                  <Button 
                    variant="danger" 
                    size="lg"
                    onClick={stopScanner}
                  >
                    <XCircleFill className="me-2" />
                    ArrÃªter le Scanner
                  </Button>
                )}
              </div>

              {/* Manual Input Form */}
              {showManualInput && (
                <div className="mb-4">
                  <Card className="border-warning">
                    <Card.Header className="bg-warning text-dark">
                      <h6 className="mb-0">ğŸ“ Saisie manuelle du code QR</h6>
                    </Card.Header>
                    <Card.Body>
                      <Form onSubmit={handleManualSubmit}>
                        <Form.Group className="mb-3">
                          <Form.Label>Code QR ou ID de l'Ã©lÃ¨ve</Form.Label>
                          <Form.Control
                            type="text"
                            placeholder="Ex: STUDENT_ID_123 ou 123"
                            value={manualQrCode}
                            onChange={(e) => setManualQrCode(e.target.value)}
                            disabled={isLoading}
                          />
                          <Form.Text className="text-muted">
                            Saisissez le code QR ou l'ID numÃ©rique de l'Ã©lÃ¨ve
                          </Form.Text>
                        </Form.Group>
                        <div className="d-flex gap-2">
                          <Button 
                            type="submit" 
                            variant="success"
                            disabled={isLoading || !manualQrCode.trim()}
                          >
                            {isLoading ? (
                              <>
                                <Spinner size="sm" className="me-2" />
                                Traitement...
                              </>
                            ) : (
                              <>
                                âœ… Valider
                              </>
                            )}
                          </Button>
                          <Button 
                            type="button" 
                            variant="outline-secondary"
                            onClick={() => {
                              setManualQrCode('');
                              setShowManualInput(false);
                            }}
                            disabled={isLoading}
                          >
                            Annuler
                          </Button>
                        </div>
                      </Form>
                    </Card.Body>
                  </Card>
                </div>
              )}

              {/* Video preview */}
              <div className="scanner-container" style={{ position: 'relative', maxWidth: '400px', margin: '0 auto' }}>
                <video
                  ref={videoRef}
                  style={{
                    width: '100%',
                    height: 'auto',
                    border: '2px solid #dee2e6',
                    borderRadius: '8px',
                    display: isScanning ? 'block' : 'none'
                  }}
                />
                {!isScanning && (
                  <div 
                    style={{
                      width: '100%',
                      height: '300px',
                      border: '2px dashed #dee2e6',
                      borderRadius: '8px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f8f9fa'
                    }}
                  >
                    <div className="text-center text-muted d-flex flex-column align-items-center">
                      <QrCodeScan size={48} />
                      <p className="mt-2 mb-0">Cliquez sur "DÃ©marrer le Scanner"</p>
                    </div>
                  </div>
                )}
              </div>
            </Card.Body>
          </Card>
        </Col>

          {/* Statistics Panel */}
        <Col lg={6}>
          <Row className="mb-3">
            <Col>
              <Card className="border-info">
                <Card.Header className="bg-info">
                  <h6 className="mb-0">ğŸ“Š Statistiques du Jour</h6>
                </Card.Header>
                <Card.Body>
                  {entryExitStats ? (
                    <Row>
                      <Col xs={4} className="text-center">
                        <div className="d-flex flex-column">
                          <div className="text-success fs-4 fw-bold">{entryExitStats.global_stats.total_entries}</div>
                          <small className="text-muted">EntrÃ©es</small>
                        </div>
                      </Col>
                      <Col xs={4} className="text-center">
                        <div className="d-flex flex-column">
                          <div className="text-primary fs-4 fw-bold">{entryExitStats.global_stats.currently_present}</div>
                          <small className="text-muted">PrÃ©sents</small>
                        </div>
                      </Col>
                      <Col xs={4} className="text-center">
                        <div className="d-flex flex-column">
                          <div className="text-danger fs-4 fw-bold">{entryExitStats.global_stats.total_exits}</div>
                          <small className="text-muted">Sorties</small>
                        </div>
                      </Col>
                    </Row>
                  ) : (
                    <div className="text-center text-muted">
                      <Spinner size="sm" className="me-2" />
                      Chargement des statistiques...
                    </div>
                  )}
                </Card.Body>
                <Card.Footer className="text-center">
                  <Button
                    variant="warning"
                    size="sm"
                    onClick={handleMarkAllAbsent}
                    disabled={isMarkingAbsent || isLoading}
                  >
                    {isMarkingAbsent ? (
                      <>
                        <Spinner size="sm" className="me-2" />
                        Marquage en cours...
                      </>
                    ) : (
                      <>
                        ğŸ“‹ Marquer les absents
                      </>
                    )}
                  </Button>
                  <div className="mt-2">
                    <small className="text-muted">
                      Marque comme absents tous les Ã©lÃ¨ves sans entrÃ©e aujourd'hui
                    </small>
                  </div>
                </Card.Footer>
              </Card>
            </Col>
          </Row>

          {/* Today's Attendance List */}
          <Card>
            <Card.Header>
              <h5 className="mb-0 d-flex align-items-center">
                <Calendar className="me-2" />
                PrÃ©sences d'Aujourd'hui
                <Badge bg="primary" className="ms-2">
                  {todayAttendances.length}
                </Badge>
              </h5>
            </Card.Header>
            <Card.Body>
              {isLoading ? (
                <div className="text-center py-4">
                  <Spinner />
                  <p className="mt-2 text-muted">Chargement...</p>
                </div>
              ) : todayAttendances.length > 0 ? (
                <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                  <Table striped hover size="sm">
                    <thead>
                      <tr>
                        <th>Ã‰lÃ¨ve</th>
                        <th>Classe</th>
                        <th>Type</th>
                        <th>Heure</th>
                        <th>Statut</th>
                      </tr>
                    </thead>
                    <tbody>
                      {todayAttendances.map((attendance, index) => (
                        <tr key={index}>
                          <td>{attendance.student?.full_name || 'N/A'}</td>
                          <td>{attendance.school_class?.name || 'N/A'}</td>
                          <td>
                            {attendance.is_present ? (
                              <Badge bg={attendance.event_type === 'entry' ? 'success' : 'info'}>
                                {attendance.event_type === 'entry' ? (
